import React, { useState } from 'react';
import '../styles/SearchPage.css';
import Header from '../components/Header';
import Footer from '../components/Footer';

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Mock search results for demonstration
    const results = [
      { title: 'Result 1', description: 'Description of result 1', link: '#' },
      { title: 'Result 2', description: 'Description of result 2', link: '#' },
      { title: 'Result 3', description: 'Description of result 3', link: '#' },
    ];
    setSearchResults(results);
  };

  return (
    <div className="page-container">
      <Header />
      <div className="content-wrap">
        <div className="search-page">
          <div className="search-container">
            <form className="search-form" onSubmit={handleSearchSubmit}>
              <select className="dropdown-menu">
                <option value="all">All</option>
                <option value="category1">Category 1</option>
                <option value="category2">Category 2</option>
              </select>
              <input
                type="text"
                className="search-input"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearchChange}
              />
              <input
                type="text"
                className="keywords-input"
                placeholder="Keywords..."
              />
              <button type="submit" className="search-button">
                <i className="flaticon-suntour-search"></i>
              </button>
            </form>
          </div>
          <div className="search-results">
            {searchResults.map((result, index) => (
              <div key={index} className="search-result-item">
                <a href={result.link} className="search-result-title">{result.title}</a>
                <p className="search-result-description">{result.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default SearchPage;
