import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/SearchPage.css';
import Header from '../components/Header';
import Footer from '../components/Footer';

const AddPlace = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const navigate = useNavigate();

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    // Fetch search results from the backend
    try {
      const response = await fetch(`http://localhost:3001/search?query=${searchQuery}`);
      const results = await response.json();
      navigate('/searchresult', { state: { results } });
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleRatingChange = (e) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      rating: parseInt(e.target.value),
    }));
  };

  const [formData, setFormData] = useState({
    placeName: '',
    address: '',
    description: '',
    rating: '',
    photos: [],
    mapUrl: '',
    phone: '',
    accessibility: '',
    amenities: '',
    children: '',
    pets: ''
  });

  const defaultImage = '/path/to/default/image.jpg'; // Replace with your default image path


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const convenience = {
      accessibility: formData.accessibility.split(',').map(item => item.trim()),
      amenities: formData.amenities.split(',').map(item => item.trim()),
      children: formData.children.split(',').map(item => item.trim()),
      pets: formData.pets.split(',').map(item => item.trim()),
    };



    const placeData = {
      placeName: formData.placeName,
      address: formData.address,
      description: formData.description,
      rating: formData.rating,
      photos: formData.photos.split(',').map(photo => photo.trim()),
      location: {
        mapUrl: formData.mapUrl,
        phone: formData.phone
      },
      convenience
    };

    try {
      const response = await fetch('http://localhost:3001/place', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(placeData),
      });
      const data = await response.json();
      if (response.ok) {
        alert('Place added successfully!');
      } else {
