import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/SearchPage.css';
import Header from '../components/Header';
import Footer from '../components/Footer';

const AddPlace = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [amenities, setAmenities] = useState([]);
    const [newAmenity, setNewAmenity] = useState('');
    const navigate = useNavigate();

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    // Fetch search results from the backend
    try {
      const response = await fetch(`http://localhost:3001/search?query=${searchQuery}`);
      const results = await response.json();
      navigate('/searchresult', { state: { results } });
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleRatingChange = (e) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      rating: parseInt(e.target.value),
    }));
  };

  const [formData, setFormData] = useState({
    placeName: '',
    address: '',
    description: '',
    rating: '',
    photos: '',
    mapUrl: '',
    phone: '',
    accessibility: [],
    amenities: [],
    children: [],
    pets: [],
    finalTags: [],
});

const [newItem, setNewItem] = useState({
  newAccessibility: '',
  newAmenity: '',
  newChildren: '',
  newPets: '',
  newTag: '',
});


const handleChange = (e) => {
  const { name, value } = e.target;
  setFormData({ ...formData, [name]: value });
};

const handleAddItem = (type) => {
  const newItemValue = newItem[`new${type}`].trim();
  if (newItemValue) {
      setFormData((prevFormData) => ({
          ...prevFormData,
          [type.toLowerCase()]: [...prevFormData[type.toLowerCase()], newItemValue],
      }));
      setNewItem({ ...newItem, [`new${type}`]: '' });
  }
};

const handleRemoveItem = (type, indexToRemove) => {
  setFormData((prevFormData) => ({
      ...prevFormData,
      [type.toLowerCase()]: prevFormData[type.toLowerCase()].filter((_, index) => index !== indexToRemove),
  }));
};

const handleSubmit = async (e) => {
  e.preventDefault();
  const convenience = {
      accessibility: formData.accessibility,
      amenities: formData.amenities,
      children: formData.children,
      pets: formData.pets,
  };

  const placeData = {
      placeName: formData.placeName,
      address: formData.address,
      description: formData.description,
