import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ImageSlide from './ImageSlider'; // Assuming you have an ImageSlider component

const PlaceDetails = () => {
  const { id } = useParams(); // Get the place ID from the URL parameters
  const [placeDetails, setPlaceDetails] = useState(null);
  const defaultImage = '/img/unnamed.jpg';

  useEffect(() => {
    const fetchPlaceDetails = async () => {
      try {
        // Fetching details for the place with the given ID
        const response = await axios.get(`/PlaceDetails/${id}`);
        if (!response.status === 200) {
          throw new Error('Network response was not ok');
        }
        const data = await response.data;
        setPlaceDetails(data);
      } catch (error) {
        console.error('Error fetching place details:', error);
      }
    };
    fetchPlaceDetails();
  }, [id]);

  if (!placeDetails) {
    return <div>Loading...</div>;
  }

  const slides = placeDetails.photos.map((photo) => ({ url: photo }));

  return (
    <div>
      <Header />
      <div className="place-details-content">
        <h1>{placeDetails.name}</h1>
        <ImageSlide slides={slides} />
        {/* Add more place details here for testing */}
      </div>
      <Footer />
    </div>
  );
};

export default PlaceDetails;
